import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import { format, parse, isValid } from 'date-fns';
import axios from 'axios';

function SmartEventEntry() {
  const [events, setEvents] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [chatHistory, setChatHistory] = useState([
    {
      sender: 'assistant',
      text: 'Welcome to Smart Event Entry! Try typing something like "Dinner with Sam on May 1st at 7pm" or "cancel Dinner with Sam" to remove an event.'
    }
  ]);

  // Load saved events from local storage
  useEffect(() => {
    const savedEvents = localStorage.getItem('events');
    if (savedEvents) {
      // Convert string dates back to Date objects
      const parsedEvents = JSON.parse(savedEvents).map(event => ({
        ...event,
        start: new Date(event.start),
        end: new Date(event.end)
      }));
      setEvents(parsedEvents);
    }
  }, []);

  // Save events to local storage when updated
  useEffect(() => {
    if (events.length > 0) {
      localStorage.setItem('events', JSON.stringify(events));
    }
  }, [events]);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && inputText.trim()) {
      handleSubmit();
    }
  };

  // Function to parse natural language input and extract event details
  const parseEventText = (text) => {
    // Check if this is a cancellation request
    const cancelMatch = text.toLowerCase().match(/cancel\s+(.*)/i);
    if (cancelMatch) {
      return { action: 'cancel', eventToCancel: cancelMatch[1] };
    }

    // Extract potential date and time information
    const dateMatch = text.match(/(\d{1,2}\/\d{1,2}\/\d{4}|\d{1,2}\/\d{1,2}|\w+ \d{1,2}(st|nd|rd|th)?)/i);
    const timeMatch = text.match(/(\d{1,2}:\d{2}(?:\s*[ap]m)?|\d{1,2}\s*[ap]m)/i);

    let title = text;
    let start = new Date();
    let end = new Date();

    // If we have a date match, parse it
    if (dateMatch) {
      const dateStr = dateMatch[0];
      // Try different formats based on the match
      let parsedDate;

      if (dateStr.includes('/')) {
        // Try MM/DD/YYYY
        parsedDate = parse(dateStr, 'MM/dd/yyyy', new Date());
        if (!isValid(parsedDate)) {
          // Try MM/DD format for current year
          parsedDate = parse(dateStr, 'MM/dd', new Date());
        }
      } else {
        // Try "May 1st" format
        const normalizedDateStr = dateStr.replace(/(st|nd|rd|th)/, '');
        parsedDate = parse(normalizedDateStr, 'MMMM d', new Date());
      }

      if (isValid(parsedDate)) {
        start = parsedDate;
        end = parsedDate;
      }
    }

    // If we have a time match, parse it
    if (timeMatch) {
      const timeStr = timeMatch[0].toLowerCase();
      let hours = 0;
      let minutes = 0;

      if (timeStr.includes(':')) {
        // Format like "7:00pm"
        const [hourStr, minuteStr] = timeStr.split(':');
        hours = parseInt(hourStr, 10);
        minutes = parseInt(minuteStr, 10);

        if (minuteStr.includes('pm') && hours < 12) {
          hours += 12;
        }
      } else {
        // Format like "7pm"
        const hourStr = timeStr.replace(/[^\d]/g, '');
        hours = parseInt(hourStr, 10);

        if (timeStr.includes('pm') && hours < 12) {
          hours += 12;
        }
      }

      start.setHours(hours, minutes, 0, 0);
      end.setHours(hours + 1, minutes, 0, 0); // Default to 1 hour duration
    }

    // Create a concise title by removing date/time information
    title = text
      .replace(dateMatch ? dateMatch[0] : '', '')
      .replace(timeMatch ? timeMatch[0] : '', '')
      .replace(/\s+/g, ' ')
      .trim();

    return { action: 'add', title, start, end };
  };

  const handleSubmit = async () => {
    if (!inputText.trim() || isProcessing) return;

    // Add user message to chat
    setChatHistory(prev => [
      ...prev,
      { sender: 'user', text: inputText }
    ]);

    setIsProcessing(true);

    try {
      // Parse the input text to determine action and details
      const parsedInput = parseEventText(inputText);

      if (parsedInput.action === 'cancel') {
        // Handle event cancellation
        const eventTitle = parsedInput.eventToCancel.toLowerCase();
        const eventIndex = events.findIndex(event => 
          event.title.toLowerCase().includes(eventTitle)
        );

        if (eventIndex !== -1) {
          const canceledEvent = events[eventIndex];
          const newEvents = [...events];
          newEvents.splice(eventIndex, 1);
          setEvents(newEvents);

          setChatHistory(prev => [
            ...prev,
            { 
              sender: 'assistant', 
              text: `Removed "${canceledEvent.title}" from your calendar.`
            }
          ]);
        } else {
          setChatHistory(prev => [
            ...prev,
            { 
              sender: 'assistant', 
              text: `I couldn't find an event matching "${parsedInput.eventToCancel}" in your calendar.`
            }
          ]);
        }
      } else {
        // Handle event addition
        const newEvent = {
          title: parsedInput.title,
          start: parsedInput.start,
          end: parsedInput.end
        };

        setEvents(prev => [...prev, newEvent]);

        // Format the dates for display
        const startFormatted = format(newEvent.start, 'EEEE, MMMM d, yyyy h:mm a');
        const endFormatted = format(newEvent.end, 'h:mm a');

        setChatHistory(prev => [
          ...prev,
          { 
            sender: 'assistant', 
            text: `Added "${newEvent.title}" to your calendar on ${startFormatted} to ${endFormatted}.`
          }
        ]);
      }
    } catch (error) {
      console.error('Error processing event:', error);

      setChatHistory(prev => [
        ...prev,
        { 
          sender: 'assistant', 
          text: 'Sorry, I had trouble understanding that. Could you try rephrasing?'
        }
      ]);
    } finally {
      setIsProcessing(false);
      setInputText('');
    }
  };

  const localizer = momentLocalizer(moment);

  return (
    <div className="flex flex-col h-screen">
      <div className="flex-1 p-6 overflow-hidden">
        {/* Calendar view */}
        <div className="bg-white shadow-md rounded-lg p-4 h-full">
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: '100%' }}
            views={['month', 'week', 'day']}
          />
        </div>
      </div>

      {/* Chat interface */}
      <div className="bg-white shadow-md h-72 border-t">
        <div className="flex flex-col h-full">
          <div className="flex-1 p-4 overflow-y-auto">
            {chatHistory.map((message, index) => (
              <div
                key={index}
                className={`mb-3 ${
                  message.sender === 'user' ? 'text-right' : 'text-left'
                }`}
              >
                <div
                  className={`inline-block p-3 rounded-lg ${
                    message.sender === 'user'
                      ? 'bg-primary text-white'
                      : 'bg-secondary text-gray-800'
                  }`}
                >
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="p-4 border-t flex">
            <input
              type="text"
              className="flex-1 mr-2 input"
              placeholder="Add or cancel an event..."
              value={inputText}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              disabled={isProcessing}
            />
            <button
              className={`btn btn-primary ${isProcessing ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={handleSubmit}
              disabled={!inputText.trim() || isProcessing}
            >
              {isProcessing ? 'Processing...' : 'Send'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SmartEventEntry;
