const fallbackParsing = (text) => {
  // Simple parsing logic similar to what we had before
  const lowerText = text.toLowerCase();
  let title = text;
  let start = new Date();
  let end = new Date();

  // Check if this is a cancellation request
  if (lowerText.includes('cancel') || lowerText.includes('delete') || lowerText.includes('remove')) {
    const eventToCancel = text.replace(/cancel|delete|remove/i, '').trim();
    return { action: 'cancel', eventToCancel };
  }

  // Very basic parsing logic for date
  if (lowerText.includes('tomorrow')) {
    start.setDate(start.getDate() + 1);
    end.setDate(end.getDate() + 1);
  } else if (lowerText.includes('next week')) {
    start.setDate(start.getDate() + 7);
    end.setDate(end.getDate() + 7);
  }

  // Very basic parsing logic for time
  if (lowerText.includes('at')) {
    const timeMatch = lowerText.match(/at\s+(\d{1,2})(?::(\d{2}))?\s*(am|pm)?/i);
    if (timeMatch) {
      let hours = parseInt(timeMatch[1], 10);
      const minutes = timeMatch[2] ? parseInt(timeMatch[2], 10) : 0;
      const ampm = timeMatch[3] ? timeMatch[3].toLowerCase() : null;

      if (ampm === 'pm' && hours < 12) hours += 12;
      if (ampm === 'am' && hours === 12) hours = 0;

      start.setHours(hours, minutes, 0, 0);
      end = new Date(start.getTime() + 60 * 60 * 1000); // Default 1 hour
    }
  }

  // Basic title simplification
  if (lowerText.includes('meeting')) {
    title = 'Meeting';
  } else if (lowerText.includes('lunch')) {
    title = 'Lunch';
  } else if (lowerText.includes('dinner')) {
    title = 'Dinner';
  } else if (lowerText.includes('coffee')) {
    title = 'Coffee';
  }

  // Add with whom
  const withMatch = lowerText.match(/with\s+([a-z\s]+)(?:at|tomorrow|next|on|$)/i);
  if (withMatch) {
    const withWhom = withMatch[1].trim();
    title = `${title} with ${withWhom}`;
  }

  return { action: 'add', title, start, end, notes: '', location: '' };
};

const fallbackScheduling = (tasks) => {
  // Sort by priority and deadline
  return [...tasks].sort((a, b) => {
    // First by priority (high to low)
    if (b.priority !== a.priority) {
      return b.priority - a.priority;
    }
    // Then by deadline (earlier first)
    return new Date(a.deadline) - new Date(b.deadline);
  }).map((task, index) => {
    // Create scheduled events
    const start = new Date();
    start.setHours(9 + index, 0, 0, 0); // Start at 9 AM, each task 1 hour later

    const duration = parseFloat(task.duration) || 1; // Default to 1 hour
    const end = new Date(start.getTime() + duration * 60 * 60 * 1000);

    return {
      id: task.id,
      title: task.title,
      start: start,
      end: end,
      category: task.category || 'work'
    };
  });
};

