import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import { parseISO, format } from 'date-fns';
import { generateTaskSchedule } from '/Users/wenxinmiao/Desktop/PlanWise/client/src/Services/openAIservice.js';

// Task categories for color coding
const TASK_CATEGORIES = [
  { id: 'work', name: 'Work', color: '#4CAF50' },
  { id: 'study', name: 'Study', color: '#2196F3' },
  { id: 'exercise', name: 'Exercise', color: '#FF9800' },
  { id: 'leisure', name: 'Leisure', color: '#9C27B0' },
  { id: 'personal', name: 'Personal', color: '#607D8B' },
];

// Priority levels
const PRIORITY_LEVELS = [
  { id: 'high', name: 'High', value: 3 },
  { id: 'medium', name: 'Medium', value: 2 },
  { id: 'low', name: 'Low', value: 1 },
];

function AutoTaskScheduler() {
  const [tasks, setTasks] = useState([]);
  const [scheduledEvents, setScheduledEvents] = useState([]);
  const [newTask, setNewTask] = useState({
    name: '',
    deadline: format(new Date(), 'yyyy-MM-dd'),
    estimatedHours: 1,
    category: TASK_CATEGORIES[0],
    priority: PRIORITY_LEVELS[1], // Default to medium priority
  });
  const [isScheduling, setIsScheduling] = useState(false);
  const [schedulingMessage, setSchedulingMessage] = useState('');

  // Load saved tasks and scheduled events from local storage
  useEffect(() => {
    const savedTasks = localStorage.getItem('tasks');
    const savedScheduledEvents = localStorage.getItem('scheduledEvents');

    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }

    if (savedScheduledEvents) {
      // Convert string dates back to Date objects
      const parsedEvents = JSON.parse(savedScheduledEvents).map(event => ({
        ...event,
        start: new Date(event.start),
        end: new Date(event.end),
      }));
      setScheduledEvents(parsedEvents);
    }
  }, []);

  // Save tasks and scheduled events to local storage when updated
  useEffect(() => {
    if (tasks.length > 0) {
      localStorage.setItem('tasks', JSON.stringify(tasks));
    }

    if (scheduledEvents.length > 0) {
      localStorage.setItem('scheduledEvents', JSON.stringify(scheduledEvents));
    }
  }, [tasks, scheduledEvents]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'category') {
      const selectedCategory = TASK_CATEGORIES.find(cat => cat.id === value);
      setNewTask({ ...newTask, category: selectedCategory });
    } else if (name === 'priority') {
      const selectedPriority = PRIORITY_LEVELS.find(p => p.id === value);
      setNewTask({ ...newTask, priority: selectedPriority });
    } else if (name === 'estimatedHours') {
      setNewTask({ ...newTask, [name]: Math.max(0.5, parseFloat(value)) });
    } else {
      setNewTask({ ...newTask, [name]: value });
    }
  };

  const handleAddTask = (e) => {
    e.preventDefault();

    if (!newTask.name.trim() || !newTask.deadline) {
      return;
    }

    // Add task with a unique ID
    const taskToAdd = {
      ...newTask,
      id: Date.now().toString(),
    };

    setTasks([...tasks, taskToAdd]);

    // Reset form
    setNewTask({
      name: '',
      deadline: format(new Date(), 'yyyy-MM-dd'),
      estimatedHours: 1,
      category: TASK_CATEGORIES[0],
      priority: PRIORITY_LEVELS[1],
    });
  };

  const handleRemoveTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const handleScheduleTasks = async () => {
    if (tasks.length === 0) return;

    setIsScheduling(true);
    setSchedulingMessage('Analyzing tasks and generating optimal schedule...');

    try {
      // Generate AI-optimized schedule
      const scheduledTasks = await generateTaskSchedule(tasks);

      // Convert the returned schedule to calendar events
      const calendarEvents = scheduledTasks.map(task => {
        return {
          title: task.title,
          start: new Date(task.start),
          end: new Date(task.end),
          category: task.category,
          notes: task.notes || ''
        };
      });

      setScheduledEvents(calendarEvents);
      setSchedulingMessage('Schedule optimized based on your priorities and deadlines!');

      // Clear the message after a few seconds
      setTimeout(() => {
        setSchedulingMessage('');
      }, 5000);
    } catch (error) {
      console.error('Error scheduling tasks:', error);
      setSchedulingMessage('There was an error generating your schedule. Please try again.');

      // Clear the error message after a few seconds
      setTimeout(() => {
        setSchedulingMessage('');
      }, 5000);
    } finally {
      setIsScheduling(false);
    }
  };

  // Custom event styling for the calendar
  const eventStyleGetter = (event) => {
    const backgroundColor = event.category ? event.category.color : '#039be5';

    return {
      style: {
        backgroundColor,
        borderRadius: '4px',
        color: '#fff',
        border: 'none',
        display: 'block',
      },
    };
  };

  const localizer = momentLocalizer(moment);

  return (
    <div className="flex flex-col md:flex-row h-screen">
      {/* Task input and list */}
      <div className="md:w-1/3 p-6 bg-white shadow-md overflow-y-auto">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Auto Task Scheduler</h2>

        <form onSubmit={handleAddTask} className="mb-6">
          <div className="form-group">
            <label htmlFor="task-name" className="form-label">Task Name</label>
            <input
              type="text"
              id="task-name"
              name="name"
              className="input"
              value={newTask.name}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="deadline" className="form-label">Deadline</label>
            <input
              type="date"
              id="deadline"
              name="deadline"
              className="input"
              value={newTask.deadline}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="estimated-hours" className="form-label">Estimated Hours</label>
            <input
              type="number"
              id="estimated-hours"
              name="estimatedHours"
              min="0.5"
              step="0.5"
              className="input"
              value={newTask.estimatedHours}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="category" className="form-label">Category</label>
            <select
              id="category"
              name="category"
              className="input"
              value={newTask.category.id}
              onChange={handleInputChange}
            >
              {TASK_CATEGORIES.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="priority" className="form-label">Priority</label>
            <select
              id="priority"
              name="priority"
              className="input"
              value={newTask.priority.id}
              onChange={handleInputChange}
            >
              {PRIORITY_LEVELS.map(priority => (
                <option key={priority.id} value={priority.id}>
                  {priority.name}
                </option>
              ))}
            </select>
          </div>

          <button type="submit" className="btn btn-primary w-full">
            Add Task
          </button>
        </form>

        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-800">Task List</h3>
            <button
              className={`btn btn-accent ${(tasks.length === 0 || isScheduling) ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={handleScheduleTasks}
              disabled={tasks.length === 0 || isScheduling}
            >
              {isScheduling ? 'Scheduling...' : 'Schedule Tasks'}
            </button>
          </div>

          {schedulingMessage && (
            <div className={`p-3 rounded-md mb-4 ${schedulingMessage.includes('error') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
              {schedulingMessage}
            </div>
          )}

          {tasks.length === 0 ? (
            <p className="text-gray-500">No tasks added yet. Add some tasks to schedule.</p>
          ) : (
            <ul className="space-y-3">
              {tasks.map(task => (
                <li
                  key={task.id}
                  className="bg-gray-50 border rounded-md p-3 flex justify-between items-center"
                >
                  <div>
                    <div className="font-medium">{task.name}</div>
                    <div className="text-sm text-gray-500">
                      Due: {format(new Date(task.deadline), 'MMM d, yyyy')} | 
                      {task.estimatedHours} hour{task.estimatedHours !== 1 ? 's' : ''}
                    </div>
                    <div className="flex items-center mt-1">
                      <span
                        className="inline-block w-3 h-3 rounded-full mr-1"
                        style={{ backgroundColor: task.category.color }}
                      ></span>
                      <span className="text-xs text-gray-600">{task.category.name}</span>
                      <span className="text-xs ml-2 px-2 py-1 rounded bg-gray-200">
                        {task.priority.name}
                      </span>
                    </div>
                  </div>
                  <button
                    type="button"
                    className="text-red-500 hover:text-red-700"
                    onClick={() => handleRemoveTask(task.id)}
                  >
                    <svg
                      className="h-5 w-5"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                      />
                    </svg>
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Calendar view */}
      <div className="md:w-2/3 p-6 flex-1 overflow-hidden">
        <div className="bg-white shadow-md rounded-lg p-4 h-full">
          <Calendar
            localizer={localizer}
            events={scheduledEvents}
            startAccessor="start"
            endAccessor="end"
            style={{ height: '100%' }}
            views={['week', 'day']}
            defaultView="week"
            eventPropGetter={eventStyleGetter}
          />
        </div>
      </div>
    </div>
  );
}

export default AutoTaskScheduler;
