import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import { format, parse, isValid } from 'date-fns';
import SmartEventEntryHeader from '.SmartEventEntryHeader';


function SmartEventEntry({ user, membership, logoutUser }) {
  const [events, setEvents] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [chatHistory, setChatHistory] = useState([
    {
      sender: 'assistant',
      text: 'Welcome to PlanWise! Try typing something like "Dinner with Sam tomorrow at 7pm" or "cancel Dinner with Sam" to remove an event.'
    }
  ]);
  const [view, setView] = useState('month');
  const [date, setDate] = useState(new Date());

  // Load saved events from localStorage based on user ID
  useEffect(() => {
    if (user) {
      const userEvents = localStorage.getItem(`events_${user.username}`);
      if (userEvents) {
        // Convert string dates back to Date objects
        const parsedEvents = JSON.parse(userEvents).map(event => ({
          ...event,
          start: new Date(event.start),
          end: new Date(event.end)
        }));
        setEvents(parsedEvents);
      }
    }
  }, [user]);

  // Save events to localStorage when updated
  useEffect(() => {
    if (user && events) {
      localStorage.setItem(`events_${user.username}`, JSON.stringify(events));
    }
  }, [events, user]);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && inputText.trim()) {
      handleSubmit();
    }
  };

  // Function to parse natural language input and extract event details
  const parseEventText = (text) => {
    // Check if this is a cancellation request
    const cancelMatch = text.toLowerCase().match(/(cancel|delete|remove)\s+(.*)/i);
    if (cancelMatch) {
      return { action: 'cancel', eventToCancel: cancelMatch[2] };
    }

    // Extract potential date and time information
    const dateMatch = text.match(/(tomorrow|next week|next month|monday|tuesday|wednesday|thursday|friday|saturday|sunday|\d{1,2}\/\d{1,2}\/\d{4}|\d{1,2}\/\d{1,2}|\w+ \d{1,2}(st|nd|rd|th)?)/i);
    const timeMatch = text.match(/(\d{1,2}:\d{2}(?:\s*[ap]m)?|\d{1,2}\s*[ap]m|\d{1,2} o'clock)/i);

    let title = text;
    let start = new Date();
    let end = new Date();

    // If we have a date match, parse it
    if (dateMatch) {
      const dateStr = dateMatch[0].toLowerCase();
      let parsedDate = new Date();

      if (dateStr.includes('tomorrow')) {
        parsedDate.setDate(parsedDate.getDate() + 1);
      } else if (dateStr.includes('next week')) {
        parsedDate.setDate(parsedDate.getDate() + 7);
      } else if (dateStr.includes('next month')) {
        parsedDate.setMonth(parsedDate.getMonth() + 1);
      } else if (['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'].includes(dateStr)) {
        // Map day names to day numbers (0 = Sunday, 1 = Monday, etc.)
        const days = {
          'sunday': 0, 'monday': 1, 'tuesday': 2, 'wednesday': 3, 
          'thursday': 4, 'friday': 5, 'saturday': 6
        };

        const targetDay = days[dateStr];
        const currentDay = parsedDate.getDay();
        let daysToAdd = targetDay - currentDay;

        // If the day is today or has already passed this week, go to next week
        if (daysToAdd <= 0) {
          daysToAdd += 7;
        }

        parsedDate.setDate(parsedDate.getDate() + daysToAdd);
      } else if (dateStr.includes('/')) {
        // Try MM/DD/YYYY
        const dateElements = dateStr.split('/').map(Number);
        if (dateElements.length === 3) {
          parsedDate = new Date(dateElements[2], dateElements[0] - 1, dateElements[1]);
        } else if (dateElements.length === 2) {
          // Try MM/DD format for current year
          parsedDate = new Date(new Date().getFullYear(), dateElements[0] - 1, dateElements[1]);
        }
      } else {
        // Try "May 1st" format
        const normalizedDateStr = dateStr.replace(/(st|nd|rd|th)/, '');
        const attemptedParse = parse(normalizedDateStr, 'MMMM d', new Date());
        if (isValid(attemptedParse)) {
          parsedDate = attemptedParse;
        }
      }

      start = parsedDate;
      end = new Date(parsedDate);
    }

    // If we have a time match, parse it
    if (timeMatch) {
      const timeStr = timeMatch[0].toLowerCase();
      let hours = 0;
      let minutes = 0;

      if (timeStr.includes(':')) {
        // Format like "7:00pm"
        const [hourStr, minuteStr] = timeStr.split(':');
        hours = parseInt(hourStr, 10);
        minutes = parseInt(minuteStr, 10);

        if (minuteStr.includes('pm') && hours < 12) {
          hours += 12;
        }
      } else if (timeStr.includes("o'clock")) {
        // Format like "7 o'clock"
        const hourStr = timeStr.replace(/[^\d]/g, '');
        hours = parseInt(hourStr, 10);

        // Default to PM for common meal times
        if (hours >= 5 && hours <= 9) {
          hours += 12;
        }
      } else {
        // Format like "7pm"
        const hourStr = timeStr.replace(/[^\d]/g, '');
        hours = parseInt(hourStr, 10);

        if (timeStr.includes('pm') && hours < 12) {
          hours += 12;
        }
      }

      start.setHours(hours, minutes, 0, 0);
      end.setHours(hours + 1, minutes, 0, 0); // Default to 1 hour duration
    } else {
      // Default times for certain events if no time specified
      if (title.toLowerCase().includes('dinner')) {
        start.setHours(19, 0, 0, 0);
        end.setHours(20, 30, 0, 0);
      } else if (title.toLowerCase().includes('lunch')) {
        start.setHours(12, 0, 0, 0);
        end.setHours(13, 0, 0, 0);
      } else if (title.toLowerCase().includes('breakfast')) {
        start.setHours(8, 0, 0, 0);
        end.setHours(9, 0, 0, 0);
      } else if (title.toLowerCase().includes('meeting')) {
        start.setHours(10, 0, 0, 0);
        end.setHours(11, 0, 0, 0);
      }
    }

    // Create a concise title by removing date/time information
    title = text
      .replace(dateMatch ? dateMatch[0] : '', '')
      .replace(timeMatch ? timeMatch[0] : '', '')
      .replace(/\s+/g, ' ')
      .trim();

    return { action: 'add', title, start, end };
  };

  const handleSubmit = async () => {
    if (!inputText.trim() || isProcessing) return;

    // Add user message to chat
    setChatHistory(prev => [
      ...prev,
      { sender: 'user', text: inputText }
    ]);

    setIsProcessing(true);

    try {
      // Parse the input text to determine action and details
      const parsedInput = parseEventText(inputText);

      if (parsedInput.action === 'cancel') {
        // Handle event cancellation
        const eventTitle = parsedInput.eventToCancel.toLowerCase();
        const eventIndex = events.findIndex(event => 
          event.title.toLowerCase().includes(eventTitle)
        );

        if (eventIndex !== -1) {
          const canceledEvent = events[eventIndex];
          // Create a new array without the canceled event
          const newEvents = events.filter((_, idx) => idx !== eventIndex);
          setEvents(newEvents);

          setChatHistory(prev => [
            ...prev,
            { 
              sender: 'assistant', 
              text: `Removed "${canceledEvent.title}" from your calendar.`
            }
          ]);
        } else {
          setChatHistory(prev => [
            ...prev,
            { 
              sender: 'assistant', 
              text: `Couldn't find an event matching "${parsedInput.eventToCancel}" in your calendar.`
            }
          ]);
        }
      } else {
        // Handle event addition
        const newEvent = {
          id: Date.now(), // Add a unique ID to each event
          title: parsedInput.title,
          start: parsedInput.start,
          end: parsedInput.end
        };

        setEvents(prev => [...prev, newEvent]);

        // Format the dates for display
        const startFormatted = format(newEvent.start, 'EEEE, MMMM d, yyyy h:mm a');
        const endFormatted = format(newEvent.end, 'h:mm a');

        setChatHistory(prev => [
          ...prev,
          { 
            sender: 'assistant', 
            text: `Added "${newEvent.title}" to your calendar on ${startFormatted} to ${endFormatted}.`
          }
        ]);
      }
    } catch (error) {
      console.error('Error processing event:', error);

      setChatHistory(prev => [
        ...prev,
        { 
          sender: 'assistant', 
          text: 'Sorry, I had trouble understanding that. Could you try rephrasing?'
        }
      ]);
    } finally {
      setIsProcessing(false);
      setInputText('');
    }
  };

  // Handle calendar navigation and view changes
  const handleNavigate = (newDate) => {
    setDate(newDate);
  };

  const handleViewChange = (newView) => {
    setView(newView);
  };

  // Clear all events function
  const clearAllEvents = () => {
    setEvents([]);
    localStorage.removeItem(`events_${user.username}`);
    setChatHistory(prev => [
      ...prev,
      { 
        sender: 'assistant', 
        text: 'All events have been cleared from your calendar.'
      }
    ]);
  };

  const localizer = momentLocalizer(moment);

  return (
    <div className="flex flex-col h-screen">
      <SmartEventEntryHeader user={user} logoutUser={logoutUser} membership={membership} />

      <div className="flex-1 p-6 overflow-hidden">
        {/* Calendar view */}
        <div className="bg-white shadow-md rounded-lg p-4 h-full">
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-2xl font-bold">
              {user ? `${user.firstName}'s Calendar` : 'My Calendar'}
            </h1>
            <button 
              className="btn btn-secondary btn-sm"
              onClick={clearAllEvents}
            >
              Clear All Events
            </button>
          </div>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: 'calc(100% - 40px)' }}
            views={['month', 'week', 'day']}
            view={view}
            date={date}
            onNavigate={handleNavigate}
            onView={handleViewChange}
            popup
          />
        </div>
      </div>

      {/* Chat interface */}
      <div className="bg-white shadow-md h-72 border-t">
        <div className="flex flex-col h-full">
          <div className="flex-1 p-4 overflow-y-auto">
            {chatHistory.map((message, index) => (
              <div
                key={index}
                className={`mb-3 ${
                  message.sender === 'user' ? 'text-right' : 'text-left'
                }`}
              >
                <div
                  className={`inline-block p-3 rounded-lg ${
                    message.sender === 'user'
                      ? 'bg-primary text-white'
                      : 'bg-secondary text-gray-800'
                  }`}
                >
                  {message.text}
                </div>
              </div>
            ))}
          </div>
          <div className="p-4 border-t flex">
            <input
              type="text"
              className="flex-1 mr-2 input"
              placeholder="Add or cancel an event..."
              value={inputText}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              disabled={isProcessing}
            />
            <button
              className={`btn btn-primary ${isProcessing ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={handleSubmit}
              disabled={!inputText.trim() || isProcessing}
            >
              {isProcessing ? 'Processing...' : 'Send'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SmartEventEntry;
