import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import axios from 'axios';
import { API_BASE_URL, API_ENDPOINTS, API_CONFIG, DEFAULT_PREFERENCES } from '../config/api';

const localizer = momentLocalizer(moment);

const AutoTaskScheduler = () => {
  const [tasks, setTasks] = useState([]);
  const [schedule, setSchedule] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    deadline: '',
    deadlineTime: '00:00',
    estimatedHours: '',
    category: 'work'
  });

  // Add logging to track form data changes
  useEffect(() => {
    console.log('Current tasks:', tasks);
    console.log('Current schedule:', schedule);
  }, [tasks, schedule]);

  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    console.log(`Input change - ${name}: "${value}" (type: ${typeof value})`);

    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form
    if (!formData.name || !formData.deadline || !formData.estimatedHours) {
      alert('Please fill in all required fields');
      return;
    }

    // Convert string values to appropriate types
    const estimatedHours = parseFloat(formData.estimatedHours);

    // Validate numeric values
    if (isNaN(estimatedHours) || estimatedHours <= 0) {
      alert('Please enter a valid positive number for estimated hours');
      return;
    }

    // Create deadline Date object
    const deadlineDateTime = `${formData.deadline}T${formData.deadlineTime}`;
    const deadlineDate = new Date(deadlineDateTime);

    // Create a new task with converted values
    const newTask = {
      id: Date.now(),
      name: formData.name,
      deadline: deadlineDate,
      estimatedHours: estimatedHours, // Now a number, not a string
      category: formData.category
    };

    console.log('Adding new task with converted values:', newTask);

    // Add the new task to the tasks array
    setTasks([...tasks, newTask]);

    // Clear the form
    setFormData({
      name: '',
      deadline: '',
      deadlineTime: '00:00',
      estimatedHours: '',
      category: 'work'
    });

    // Generate a new schedule with the updated tasks
    generateSchedule([...tasks, newTask]);
  };

  const generateSchedule = async (currentTasks) => {
    console.log('Generating schedule with tasks:', currentTasks);

    // Prepare tasks data for API
    const tasksForApi = currentTasks.map(task => ({
      id: task.id,
      name: task.name,
      deadline: task.deadline.toISOString(),
      estimatedHours: Number(task.estimatedHours), // Ensure numeric conversion
      category: task.category
    }));

    try {
      // Set loading state
      setLoading(true);
      setError(null);

      console.log('Sending to scheduling API:', tasksForApi);

      // Call the scheduling API
      const response = await axios.post(
        `${API_BASE_URL}${API_ENDPOINTS.SCHEDULE}`, 
        {
          tasks: tasksForApi,
          preferences: DEFAULT_PREFERENCES
        }, 
        API_CONFIG
      );

      const scheduleData = response.data;
      console.log('API returned schedule:', scheduleData);

      // Transform API response to calendar events
      const newSchedule = scheduleData.events.map(event => ({
        taskId: event.taskId,
        title: event.title || currentTasks.find(t => t.id === event.taskId)?.name || 'Untitled Task',
        start: new Date(event.startTime),
        end: new Date(event.endTime),
        category: event.category || currentTasks.find(t => t.id === event.taskId)?.category || 'work'
      }));

      console.log('Transformed schedule for calendar:', newSchedule);
      setSchedule(newSchedule);
    } catch (error) {
      console.error('Error calling scheduling API:', error);
      setError('Failed to generate schedule from API. Using local scheduling instead.');

      // Fallback to local scheduling if API fails
      console.log('Using fallback local scheduling method');

      // Sort tasks by deadline
      const sortedTasks = [...currentTasks].sort((a, b) => a.deadline - b.deadline);

      // Create a simple schedule based on deadlines
      const newSchedule = [];
      let currentDate = new Date();

      // Reset time to the start of the day
      currentDate.setHours(9, 0, 0, 0);

      for (const task of sortedTasks) {
        // Skip tasks that are already in the schedule
        if (newSchedule.some(event => event.taskId === task.id)) {
          continue;
        }

        // Ensure estimatedHours is a number
        const hours = Number(task.estimatedHours);

        if (isNaN(hours)) {
          console.error(`Invalid estimatedHours for task ${task.name}: ${task.estimatedHours}`);
          continue;
        }

        // Calculate end time
        const startTime = new Date(currentDate);
        const endTime = new Date(currentDate);
        endTime.setHours(endTime.getHours() + hours);

        // Create a schedule event
        newSchedule.push({
          taskId: task.id,
          title: task.name,
          start: startTime,
          end: endTime,
          category: task.category
        });

        // Move current date to the next day
        currentDate.setDate(currentDate.getDate() + 1);
        currentDate.setHours(9, 0, 0, 0);
      }

      console.log('Generated fallback schedule:', newSchedule);
      setSchedule(newSchedule);
    } finally {
      // Clear loading state
      setLoading(false);
    }
  };

  // Color coding for different categories
  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad'; // default

    switch (event.category) {
      case 'work':
        backgroundColor = '#3174ad';
        break;
      case 'study':
        backgroundColor = '#5cb85c';
        break;
      case 'personal':
        backgroundColor = '#f0ad4e';
        break;
      case 'health':
        backgroundColor = '#d9534f';
        break;
      default:
        backgroundColor = '#3174ad';
    }

    return {
      style: {
        backgroundColor,
        borderRadius: '5px',
        opacity: 0.8,
        color: 'white',
        border: '0px',
        display: 'block'
      }
    };
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Auto Task Scheduler</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-xl font-semibold mb-4">Add Task</h3>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Task Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Deadline Date</label>
              <input
                type="date"
                name="deadline"
                value={formData.deadline}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Deadline Time</label>
              <input
                type="time"
                name="deadlineTime"
                value={formData.deadlineTime}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Estimated Hours</label>
              <input
                type="number"
                name="estimatedHours"
                value={formData.estimatedHours}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                min="0.5"
                step="0.5"
                required
              />
              <span className="text-sm text-gray-500">Enter time in hours</span>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Category</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="work">Work</option>
                <option value="study">Study</option>
                <option value="personal">Personal</option>
                <option value="health">Health</option>
              </select>
            </div>

            <button
              type="submit"
              className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
            >
              Add Task
            </button>
          </form>
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-xl font-semibold mb-4">Task List</h3>

          {tasks.length === 0 ? (
            <p className="text-gray-500">No tasks added yet.</p>
          ) : (
            <ul className="divide-y">
              {tasks.map(task => (
                <li key={task.id} className="py-3">
                  <div className="font-medium">{task.name}</div>
                  <div className="text-sm text-gray-500">
                    Deadline: {moment(task.deadline).format('MMMM D, YYYY [at] h:mm A')}
                  </div>
                  <div className="text-sm text-gray-500">
                    Estimated time: {task.estimatedHours} hours
                  </div>
                  <div className="text-sm">
                    <span 
                      className={`inline-block px-2 py-1 rounded text-xs text-white ${
                        task.category === 'work' ? 'bg-blue-500' :
                        task.category === 'study' ? 'bg-green-500' :
                        task.category === 'personal' ? 'bg-yellow-500' :
                        'bg-red-500'
                      }`}
                    >
                      {task.category}
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      <div className="mt-8 bg-white p-4 rounded shadow">
        <h3 className="text-xl font-semibold mb-4">Generated Schedule</h3>

        {loading ? (
          <div className="flex justify-center items-center h-96">
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
              <p className="text-gray-600">Generating optimal schedule...</p>
            </div>
          </div>
        ) : error ? (
          <div className="mb-4">
            <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4" role="alert">
              <p>{error}</p>
            </div>
          </div>
        ) : null}

        <div className="h-96">
          <Calendar
            localizer={localizer}
            events={schedule}
            startAccessor="start"
            endAccessor="end"
            eventPropGetter={eventStyleGetter}
            views={['month', 'week', 'day']}
            defaultView="week"
          />
        </div>
      </div>
    </div>
  );
};

export default AutoTaskScheduler;
