import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';

function PaymentPage({ membership, updateMembership }) {
  const [succeeded, setSucceeded] = useState(false);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [disabled, setDisabled] = useState(true);
  const [clientSecret, setClientSecret] = useState('');
  const [cardholderName, setCardholderName] = useState('');
  const [email, setEmail] = useState('');

  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  // Determine price ID and amount based on selected membership
  const getPriceDetails = () => {
    if (membership === 'premium') {
      return {
        priceId: process.env.REACT_APP_STRIPE_PRICE_ID_MONTHLY,
        amount: '$5/month',
        description: 'Premium Monthly Membership'
      };
    } else if (membership === 'lifetime') {
      return {
        priceId: process.env.REACT_APP_STRIPE_PRICE_ID_LIFETIME,
        amount: '$80 (one-time)',
        description: 'Lifetime Membership'
      };
    }
    return { priceId: null, amount: '', description: '' };
  };

  const { priceId, amount, description } = getPriceDetails();

  useEffect(() => {
    // Create PaymentIntent as soon as the page loads
    const createPaymentIntent = async () => {
      try {
        const response = await axios.post('/api/create-payment-intent', {
          priceId: priceId
        });

        setClientSecret(response.data.clientSecret);
      } catch (error) {
        console.error('Error creating payment intent:', error);
        setError('An error occurred while setting up the payment. Please try again.');
      }
    };

    if (priceId) {
      createPaymentIntent();
    }
  }, [priceId]);

  const handleChange = (event) => {
    // Listen for changes in the CardElement
    // and display any errors as the customer types their card details
    setDisabled(event.empty);
    setError(event.error ? event.error.message : '');
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not loaded yet
      return;
    }

    // Validate form fields
    if (!cardholderName.trim()) {
      setError('Please enter the cardholder name');
      return;
    }

    if (!email.trim()) {
      setError('Please enter your email');
      return;
    }

    setProcessing(true);

    // Create payment method and confirm payment intent
    const payload = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: cardholderName,
          email: email
        }
      }
    });

    if (payload.error) {
      setError(`Payment failed: ${payload.error.message}`);
      setProcessing(false);
    } else {
      setError(null);
      setSucceeded(true);
      setProcessing(false);

      // Simulate successful payment and navigate to app
      setTimeout(() => {
        navigate('/smart-event-entry');
      }, 2000);
    }
  };

  const cardStyle = {
    style: {
      base: {
        color: '#32325d',
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: 'antialiased',
        fontSize: '16px',
        '::placeholder': {
          color: '#aab7c4'
        }
      },
      invalid: {
        color: '#fa755a',
        iconColor: '#fa755a'
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md mx-auto">
        <div className="text-center mb-10">
          <h1 className="text-3xl font-extrabold text-gray-900">
            Complete Your Purchase
          </h1>
          <p className="mt-2 text-gray-600">
            {description} - {amount}
          </p>
        </div>

        {succeeded ? (
          <div className="bg-green-50 border border-green-400 text-green-700 px-4 py-3 rounded relative mb-6">
            <strong className="font-bold">Payment successful!</strong>
            <p>Redirecting you to the app...</p>
          </div>
        ) : (
          <form className="bg-white shadow-md rounded-lg p-8" onSubmit={handleSubmit}>
            {error && (
              <div className="bg-red-50 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-6">
                {error}
              </div>
            )}

            <div className="mb-6">
              <label htmlFor="cardholderName" className="block text-sm font-medium text-gray-700 mb-2">
                Cardholder Name
              </label>
              <input
                type="text"
                id="cardholderName"
                className="input"
                value={cardholderName}
                onChange={(e) => setCardholderName(e.target.value)}
                placeholder="John Doe"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                Email
              </label>
              <input
                type="email"
                id="email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="email@example.com"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="card-element" className="block text-sm font-medium text-gray-700 mb-2">
                Credit or Debit Card
              </label>
              <div className="border border-gray-300 rounded-md p-4 bg-white">
                <CardElement id="card-element" options={cardStyle} onChange={handleChange} />
              </div>
            </div>

            <button
              type="submit"
              className="w-full btn btn-primary py-3"
              disabled={processing || disabled || succeeded}
            >
              {processing ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Processing...
                </span>
              ) : (
                `Pay ${amount}`
              )}
            </button>

            <p className="text-xs text-gray-500 mt-4">
              Your payment information is secured with SSL encryption. We do not store your credit card details.
            </p>
          </form>
        )}

        <div className="mt-6 text-center">
          <button
            type="button"
            className="text-primary hover:text-primary-dark"
            onClick={() => navigate('/membership')}
          >
            ‚Üê Back to membership selection
          </button>
        </div>
      </div>
    </div>
  );
}

export default PaymentPage;
