/**
 * Generate optimized task schedule based on priorities, deadlines, and existing calendar events
 * @param {Array} tasks - List of tasks with priorities and deadlines
 * @param {Array} events - Existing calendar events
 * @param {Object} preferences - User preferences for scheduling
 * @returns {Promise<Array>} - Optimized schedule of tasks
 */
export const optimizeTaskSchedule = async (tasks, events, preferences) => {
  try {
    // Prepare task and event data for OpenAI
    const tasksData = tasks.map(task => ({
      id: task.id,
      title: task.title,
      priority: task.priority,
      duration: parseFloat(task.duration), // Ensure it's a number
      deadline: task.deadline, // In ISO format with specific time
      category: task.category ? (task.category.id || task.category) : 'work'
    }));

    const eventsData = events.map(event => ({
      title: event.title,
      start: typeof event.start === 'string' ? event.start : event.start.toISOString(),
      end: typeof event.end === 'string' ? event.end : event.end.toISOString()
    }));

    // Extract working hours as actual times
    const workStart = preferences.workingHours.start;
    const workEnd = preferences.workingHours.end;

    // Format working hours for better readability in the prompt
    const workStartFormatted = `${workStart} (${parseInt(workStart.split(':')[0], 10)}:${workStart.split(':')[1]})`;
    const workEndFormatted = `${workEnd} (${parseInt(workEnd.split(':')[0], 10)}:${workEnd.split(':')[1]})`;

    // Create a sample working day to demonstrate the schedule
    const today = new Date();
    const workingDayStart = new Date(today);
    workingDayStart.setHours(
      parseInt(workStart.split(':')[0], 10),
      parseInt(workStart.split(':')[1], 10),
      0,
      0
    );

    const workingDayEnd = new Date(today);
    workingDayEnd.setHours(
      parseInt(workEnd.split(':')[0], 10),
      parseInt(workEnd.split(':')[1], 10),
      0,
      0
    );

    // Ensure preferences are properly formatted with explicit values
    const formattedPreferences = {
      workingHours: {
        start: workStart,
        end: workEnd,
        startFormatted: workStartFormatted,
        endFormatted: workEndFormatted
      },
      maxTasksPerDay: preferences.maxTasksPerDay || 5,
      breakBetweenTasks: preferences.breakBetweenTasks || 15, // minutes
      splitTaskThreshold: preferences.splitTaskThreshold || 2 // hours
    };

    console.log("Formatted preferences:", formattedPreferences);

    // Prepare the OpenAI prompt with explicit values instead of template literals
    const promptContent = `You are a task scheduling assistant. Optimize the user's tasks based on priorities, deadlines, and existing calendar events.

IMPORTANT SCHEDULING RULES:
1. Tasks must NEVER overlap with each other or with existing calendar events
2. Higher priority tasks should be scheduled earlier, especially those with imminent deadlines
3. If a task is longer than ${formattedPreferences.splitTaskThreshold} hours, break it into multiple smaller sessions across different time slots or days
4. Schedule ONLY ONE task per time slot - absolutely no overlapping tasks
5. Strictly honor the user's working hours: Start at ${workStartFormatted} and end at ${workEndFormatted} each day
6. Include ${formattedPreferences.breakBetweenTasks}-minute breaks between consecutive tasks
7. Schedule a maximum of ${formattedPreferences.maxTasksPerDay} tasks per day, utilizing the full working day
8. Put highest priority on meeting the deadline for each task
9. Schedule tasks efficiently to maximize the use of available time within working hours

Format your response as a JSON array of scheduled tasks, with each task having:
- id: The original task ID
- title: Task title 
- start: Start time (ISO format)
- end: End time (ISO format)
- category: Task category (use the original task's category)
- notes: Optional scheduling notes (e.g., "Part 1 of 3" for split tasks)

Each task must have a specific time slot with no overlaps.`;

    // Log the full request for debugging
    console.log("Full task data being sent to OpenAI:", JSON.stringify(tasksData, null, 2));
    console.log("Full events data being sent to OpenAI:", JSON.stringify(eventsData, null, 2));

    const response = await axios.post(
      API_ENDPOINT,
      {
        model: "gpt-4",
        messages: [
          {
            role: "system",
            content: promptContent
          },
          {
            role: "user",
            content: JSON.stringify({
              tasks: tasksData,
              events: eventsData,
              preferences: formattedPreferences
            })
          }
        ],
        temperature: 0.2, // Lower temperature for more deterministic results
        max_tokens: 1500
      },
      {
        headers: {
          'Authorization': `Bearer ${OPENAI_API_KEY}`,
          'Content-Type': 'application/json'
        }
      }
    );

    // Log the raw response for debugging
    console.log("Raw OpenAI response:", response.data.choices[0].message.content);

    // Parse and validate the OpenAI response
    let parsedSchedule;
    try {
      parsedSchedule = JSON.parse(response.data.choices[0].message.content);
      console.log("Parsed schedule result:", parsedSchedule);

      // Validate each task has required fields
      const validSchedule = parsedSchedule.every(task => 
        task.id && task.title && task.start && task.end
      );

      if (!validSchedule) {
        throw new Error("Invalid schedule format returned");
      }
    } catch (error) {
      console.error("Failed to parse OpenAI response:", error);
      return fallbackScheduling(tasks, events, preferences);
    }

    // Convert string dates to Date objects and ensure proper formatting
    return parsedSchedule.map(task => {
      const originalTask = tasks.find(t => t.id === task.id);
      let categoryValue;

      // Handle category properly with more robust fallbacks
      if (originalTask && originalTask.category) {
        categoryValue = originalTask.category;
      } else if (task.category) {
        // If task has category but we can't find original, create a category object
        if (typeof task.category === 'string') {
          // Find a matching category from the pre-defined categories if possible
          const matchingCategoryFromTasks = tasks.find(t => 
            t.category && t.category.id === task.category
          )?.category;

          if (matchingCategoryFromTasks) {
            categoryValue = matchingCategoryFromTasks;
          } else {
            // Create a basic category object
            categoryValue = { 
              id: task.category, 
              name: task.category.charAt(0).toUpperCase() + task.category.slice(1),
              color: '#4CAF50' // Default color
            };
          }
        } else {
          // Category is already an object
          categoryValue = task.category;
        }
      } else {
        // Default category as a last resort
        categoryValue = { 
          id: 'work', 
          name: 'Work',
          color: '#4CAF50'
        };
      }

      return {
        ...task,
        start: new Date(task.start),
        end: new Date(task.end),
        category: categoryValue
      };
    });
  } catch (error) {
    console.error('Error optimizing task schedule with OpenAI:', error);

    // Try the backend API
    try {
      const response = await axios.post(`${API_URL}/schedule-tasks`, { 
        tasks, 
        events, 
        preferences 
      });

      // Convert string dates to Date objects in the schedule
      return response.data.schedule.map(task => ({
        ...task,
        start: new Date(task.start),
        end: new Date(task.end)
      }));
    } catch (backendError) {
      console.error('Backend API also failed:', backendError);

      // Fallback to basic scheduling algorithm
      return fallbackScheduling(tasks, events, preferences);
    }
  }
};

