// Fallback scheduling function when APIs fail
const fallbackScheduling = (tasks, events, preferences) => {
  // Sort tasks by priority (high to low) and deadline
  const sortedTasks = [...tasks].sort((a, b) => {
    // First by priority (high to low)
    if (b.priority.value !== a.priority.value) {
      return b.priority.value - a.priority.value;
    }
    // Then by deadline (earlier first)
    return new Date(a.deadline) - new Date(b.deadline);
  });

  const scheduledTasks = [];
  const today = new Date();

  // Parse working hours
  const workStart = preferences.workingHours.start.split(':');
  const workEnd = preferences.workingHours.end.split(':');
  const workStartHour = parseInt(workStart[0], 10);
  const workStartMinute = parseInt(workStart[1], 10);
  const workEndHour = parseInt(workEnd[0], 10);
  const workEndMinute = parseInt(workEnd[1], 10);

  // Calculate break time in milliseconds
  const breakTimeMs = preferences.breakBetweenTasks * 60 * 1000;

  // Calculate max tasks per day setting
  const maxTasksPerDay = preferences.maxTasksPerDay || 5;

  // Start scheduling from today
  let currentDay = new Date(today);
  let tasksForToday = 0;

  // Set current time to working hours start
  currentDay.setHours(workStartHour, workStartMinute, 0, 0);

  for (const task of sortedTasks) {
    // Get task duration in milliseconds
    const taskDurationMs = parseFloat(task.duration) * 60 * 60 * 1000;
    const splitThresholdMs = preferences.splitTaskThreshold * 60 * 60 * 1000;

    // Check if task should be split
    if (taskDurationMs > splitThresholdMs) {
      // Calculate how many parts to split into
      const parts = Math.ceil(taskDurationMs / splitThresholdMs);
      const partDurationMs = taskDurationMs / parts;

      for (let i = 0; i < parts; i++) {
        if (tasksForToday >= maxTasksPerDay) {
          // Move to next day
          currentDay = new Date(currentDay.getTime() + 24 * 60 * 60 * 1000);
          currentDay.setHours(workStartHour, workStartMinute, 0, 0);
          tasksForToday = 0;
        }

        // Set end time
        const startTime = new Date(currentDay);
        const endTime = new Date(startTime.getTime() + partDurationMs);

        // Check if end time exceeds working hours
        const workingDayEnd = new Date(currentDay);
        workingDayEnd.setHours(workEndHour, workEndMinute, 0, 0);

        if (endTime > workingDayEnd) {
          // Move to next day
          currentDay = new Date(currentDay.getTime() + 24 * 60 * 60 * 1000);
          currentDay.setHours(workStartHour, workStartMinute, 0, 0);
          tasksForToday = 0;

          // Recalculate start and end time
          const startTime = new Date(currentDay);
          const endTime = new Date(startTime.getTime() + partDurationMs);

          scheduledTasks.push({
            id: task.id,
            title: `${task.title} (Part ${i + 1} of ${parts})`,
            start: startTime,
            end: endTime,
            category: task.category,
            notes: `Part ${i + 1} of ${parts}`
          });
        } else {
          scheduledTasks.push({
            id: task.id,
            title: `${task.title} (Part ${i + 1} of ${parts})`,
            start: startTime,
            end: endTime,
            category: task.category,
            notes: `Part ${i + 1} of ${parts}`
          });
        }

        // Increase task count and move time forward (including break)
        tasksForToday++;
        currentDay = new Date(endTime.getTime() + breakTimeMs);
      }
    } else {
      // Handle regular tasks
      if (tasksForToday >= maxTasksPerDay) {
        // Move to next day
        currentDay = new Date(currentDay.getTime() + 24 * 60 * 60 * 1000);
        currentDay.setHours(workStartHour, workStartMinute, 0, 0);
        tasksForToday = 0;
      }

      // Set end time
      const startTime = new Date(currentDay);
      const endTime = new Date(startTime.getTime() + taskDurationMs);

      // Check if end time exceeds working hours
      const workingDayEnd = new Date(currentDay);
      workingDayEnd.setHours(workEndHour, workEndMinute, 0, 0);

      if (endTime > workingDayEnd) {
        // Move to next day
        currentDay = new Date(currentDay.getTime() + 24 * 60 * 60 * 1000);
        currentDay.setHours(workStartHour, workStartMinute, 0, 0);
        tasksForToday = 0;

        // Recalculate start and end time
        const startTime = new Date(currentDay);
        const endTime = new Date(startTime.getTime() + taskDurationMs);

        scheduledTasks.push({
          id: task.id,
          title: task.title,
          start: startTime,
          end: endTime,
          category: task.category
        });
      } else {
        scheduledTasks.push({
          id: task.id,
          title: task.title,
          start: startTime,
          end: endTime,
          category: task.category
        });
      }

      // Increase task count and move time forward (including break)
      tasksForToday++;
      currentDay = new Date(endTime.getTime() + breakTimeMs);
    }
  }

  return scheduledTasks;
};

